var TableManageDefault = function () {
    "use strict";

    var runDataTable = function () {
        var table = $('#data-table');

        // âœ… Cegah inisialisasi ulang
        if (!$.fn.DataTable.isDataTable(table)) {
            table.DataTable({
                responsive: true
                // konfigurasi tambahan lainnya jika perlu
            });
        }
    };

    return {
        // public function
        init: function () {
            runDataTable();
        }
    };
}();